import {useState} from 'react'
import {View, Text, StyleSheet, SafeAreaView, Platform, TouchableOpacity, Alert, Image} from 'react-native'
import DateTimePicker from '@react-native-community/datetimepicker'
import { getAuth } from 'firebase/auth';
import { initializeApp } from 'firebase/app';
import { getFirestore, addDoc, collection, getDocs } from 'firebase/firestore'
import { firebaseConfig } from '../../../firebase-config'
import { useNavigation } from '@react-navigation/native';

export const TrocaOleoMoto = () => {
  const app = initializeApp(firebaseConfig);
  const auth = getAuth(app);
  const db = getFirestore(app)
  const navigation = useNavigation()

  const [date, setDate] = useState(new Date());
  const [mode, setMode] = useState('date');
  const [show, setShow] = useState(false);
  const uri = 'data:image/jpeg;base64,'


  const onChange = (event, selectedDate) => {
    const currentDate = selectedDate || new Date();
    setShow(false);
    setDate(currentDate);
  };

  const showMode = (currentMode) => {
    if (Platform.OS === 'android') {
      setShow(true);
    }
    setMode(currentMode);
  };

  const showDatepicker = () => {
    showMode('date');
  };

  const showTimepicker = () => {
    showMode('time');
  };

  const create = () => {
    let saveDate = `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}--${date.getHours()}:${date.getMinutes()}`
    const tableRef = collection(db, 'agendamentos')

    getDocs(tableRef).then((snapshot) => {

      let x = snapshot.docs.map(doc => doc.data())
      let result = x.filter(x => x.dataHora == saveDate)

      console.log(x)
      console.log(result)
      console.log(saveDate)
      if (result.length != 0) {
        Alert.alert('Infelizmente esse horário já está preenchido, por favor escolha outro')
      } else {
        addDoc(collection(db, "agendamentos"), {
          userId: auth.currentUser.uid,
          Categoria: "Moto",
          Servico: "Troca de Óleo",
          dataHora: saveDate,
        }).then(() => {
          console.log('data submitted');
          Alert.alert('Agendamento realizado com sucesso!')
          navigation.navigate('Dashboard')
        }).catch((error) => {
          console.log(error)
        });
      }
    })
  }

  const Form = () => {
    return (
      <View style={styles.container}>
      <Image source={{ uri }} style={[styles.image, StyleSheet.absoluteFill]}/>

      <View style={styles.login}>
      <TouchableOpacity onPress={showDatepicker} style={styles.button}><Text style={styles.textButton}>Escolha uma data</Text></TouchableOpacity>
      <TouchableOpacity onPress={showTimepicker} style={styles.button}><Text style={styles.textButton}>Escolha um horário</Text></TouchableOpacity>
      <TouchableOpacity onPress={create} style={styles.button}><Text style={styles.textButton}>Realizar Agendamento</Text></TouchableOpacity>
      {show && (
        <DateTimePicker
          testID="dateTimePicker"
          value={date}
          mode={mode}
          is24Hour={true}
          onChange={onChange}
        />
      )}
      </View>
    </View>
    )
  }

  return (
    <View style={styles.container}>
      <SafeAreaView style={styles.container}>
        <Form />
      </SafeAreaView>
    </View>
  )
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center'
  },
  button: {
    borderColor: 'black',
    borderWidth: 1,
    borderRadius: 20,
    backgroundColor: '#fff',
    width: 200,
    height: 60,
    justifyContent: 'center',
    alignItems: 'center',
    margin: 20,
  },
  textButton: {
    fontSize: 20,
    fontWeight: 'bold',
  },

  image: {
    width: '100%',
    height: '100%',
    resizeMode: 'cover',
  },

  login: {
    width: 412,
    height: 600,
    borderColor: '#000',
    borderWidth: 2,
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 35,
  },
})